#submit_order {
  .order_tip {
    width: 100%;
    box-sizing: border-box;
    padding: 22px 30px 22px 15px;
    margin-top: 20px;
    border: 1px solid #f78000;
    background: #fdf2e8;
    border-radius: 5px;
    .title {
      color: #f78000;
      font-size: 14px;
      font-weight: bold;
      i {
        margin-right: 5px;
        font-size: 20px;
      }
    }
    p {
      text-indent: 2em;
      font-size: 14px;
      color: #666;
      line-height: 24px;
    }
  }

  .ordet_info_title {
    margin-top: 10px;
    line-height: 36px;
    font-size: 16px;
    color: #444;
    display: flex;
    justify-content: space-between;
    a {
      color: #f78000;
      height: 24px;
      line-height: 36px;
      font-size: 12px;
    }
  }

  .ordet_box {
    border: 1px solid #e9e9e9;
    .ordet_info {
      .common_title {
        display: flex;
        justify-content: space-between;
        padding: 0 20px;

        background: #f2f2f2;
        font-weight: bold;
        line-height: 34px;
        border-bottom: 1px solid #e9e9e9;
        span {
          font-size: 14px;
          font-weight: bold;
          color: #444;
        }
        a {
          color: #0093e6;
          font-weight: normal;
        }
      }
      .consignee {
        .order_address {
          padding: 20px 0 20px 100px;
          .address_list.active {
            border: 3px dashed #33a9eb;
          }
          .address_list {
            display: inline-block;
            vertical-align: -webkit-baseline-middle;
            width: 250px;
            height: 116px;
            margin: 5px 10px;
            padding: 12px 20px;
            border: 3px dashed #ccc;
            .addressee {
              display: flex;
              justify-content: space-between;
              align-items: center;
              border-bottom: 1px dashed #dedede;
              line-height: 30px;
              a {
                color: #9a9a9a;
              }
              .set_normal {
                &:hover {
                  color: #0093e6;
                }
              }
            }
            .order_edit_address {
              padding: 10px 0;
              a {
                &:nth-last-of-type(1) {
                  margin-left: 10px;
                }
                &:hover {
                  color: #f00;
                }
              }
            }
            .order_address_info {
              padding-top: 5px;
              p {
                line-height: 20px;
              }
            }
          }
        }
      }

      .order_people {
        ul {
          color: #9a9a9a;
          line-height: 50px;
          padding: 0 20px;
          li {
            display: inline-block;
            margin-right: 80px;
            &:nth-last-child(1) {
              a {
                color: #0093e6;
              }
            }
          }
        }
      }

      .shipping {
        ul {
          display: flex;
          align-items: center;
          padding: 14px 0 10px 42px;
          line-height: 26px;
          li {
            display: flex;
            #freightToPay {
              vertical-align: middle;
              margin-top: 7px;
            }
            .left {
              margin-right: 10px;
            }
            .goldenrod {
              margin: 0 5px;

              color: #ff7800;
            }
            .red {
              color: #ff0000;
            }
          }
        }
      }

      .invoice_info {
        h4 {
          .red {
            color: #ff0000;
            font-size: 12px;
            font-weight: normal;
          }
        }

        .invoice_info_ul {
          padding: 22px 0 16px 20px;
          .invoice_info_btn {
            display: inline-block;
            margin-right: 40px;
            position: relative;
            box-sizing: border-box;
            width: 180px;
            height: 44px;
            line-height: 42px;
            text-align: center;
            border: 1px solid #dedede;
            a {
              display: block;
              width: 100%;
              height: 100px;
            }
          }
        }
      }

      .remark_info {
        #remark_ipt_toggle {
          padding: 22px 0 20px 42px;
          width: 680px;
          #order_remark {
            box-sizing: border-box;
            padding: 6px;
            width: 680px;
            height: 95px;
            font-size: 12px;
            border: 1px solid #ccc;
          }
          p {
            margin-top: 20px;
            text-align: right;
            .goldenrod {
              color: #f78000;
            }
          }
        }
      }

      .preferential_method {
        border-bottom: 1px solid #e9e9e9;
        h4 {
          span {
            .way {
              color: #ff0000;
              font-size: 12px;
              font-weight: normal;
            }
          }
        }
      }
    }

    .order_submit_wraper {
      height: 100%;
      display: flex;
      justify-content: space-between;
      .order_submit_info {
        width: 450px;
        text-align: right;
        ul {
          li {
            width: 100%;
            text-align: right;
            line-height: 28px;
            margin: 5px 0;
            div {
              display: inline-block;
              &:nth-last-child(1) {
                width: 150px;
              }
            }
            .total {
              font-size: 20px;
              line-height: 20px;
              font-weight: bold;
              color: #f78000;
            }
          }
        }

        .agreement {
          .el-checkbox {
            margin-right: 10px;
          }
          a {
            color: #0093e6;
          }
        }
        .el-button {
          height: 40px;
          font-size: 16px;
          color: #fff;
          text-align: center;
          border-radius: 2px;
          background: #ff7800;
          margin: 20px 0 40px;
        }
      }
    }
  }

  // 弹框公共样式
  .el-dialog {
    border: 4px solid #d9d9d9;
    .el-dialog__header {
      padding: 10px;
      border-bottom: 1px solid #dedede;
      span {
        font-size: 14px;
        font-weight: bold;
        color: #444;
      }
    }
    .el-dialog__body {
      padding-top: 10px;
      padding-bottom: 10px;
    }
  }
  .el-button {
    padding: 6px 20px;
  }
  // 新增地址弹出框
  .new_address {
    .el-dialog__wrapper {
      .el-dialog {
        width: 544px;
        .el-dialog__body {
          .el-form-item {
            margin: 0;
            .el-form-item__label {
              width: 100px;
            }
            .el-input {
              width: 260px;
              .el-input__inner {
                width: 100%;
                height: 30px;
                line-height: 30px;
                font-size: 10px;
              }
            }
            .particular_input {
              width: 360px;
            }
            .el-form-item__error {
              left: 370px;
              top: 10px;
            }
          }

          .particular_content {
            .el-form-item__error {
              top: 40px;
            }
          }
        }

        .el-dialog__footer {
          padding: 0 110px 20px;
          text-align: left;
          .el-button {
            padding: 8px 20px;
          }
        }
      }
    }
  }

  // 新增联系人弹出框
  .linkman {
    .el-dialog {
      width: 630px;
      .el-dialog__body {
        h4 {
          line-height: 20px;
          border-bottom: 1px solid #dedede;
        }
        .el-table {
          thead,
          tbody {
            th,
            td {
              text-align: center;
            }
            td > .cell {
              font-size: 10px;
            }
            .operation {
              padding: 0 5px;
              i {
                color: #f78000;
                font-size: 16px;
              }
              &:hover {
                color: #0093e6;
              }
            }
          }
        }
        .linkman_btn {
          text-align: center;
          padding-top: 5px;
        }
        .el-form {
          padding: 10px 0 15px;
          .el-form-item {
            margin-bottom: 0;
            .el-form-item__label {
              padding: 0;
            }
            .el-form-item__content {
              .el-input {
                .el-input__inner {
                  width: 120px;
                  height: 30px;
                  line-height: 30px;
                }
              }
            }
          }
        }
      }
    }
  }
}
