#list_details {
  .main-box {
    margin-top: 20px;
    // 目录
    .catalog {
      width: 240px;
      .el-menu {
        background-color: #eee;
        & > li {
          span {
            display: block;
            width: 100%;
            height: 100%;
          }
        }
      }
    }
    // 商品
    .commodity {
      margin-left: 15px;
      // 商品分类标题
      span.title {
        font-size: 14px;
        color: #9a9a9a;
        .name {
          color: #666;
        }
        .num {
          color: #ff7800;
        }
      }

      .filtrate_module {
        margin: 10px 0;
        border: 1px solid #ccc;
        .filtrate_list {
          overflow-y: scroll;
          height: 250px;
          width: 100%;
          white-space: nowrap;
          .filtrate {
            width: 150px;
            padding: 0 5px;
            display: inline-block;
            h4 {
              font-size: 14px;
              font-weight: bold;
              line-height: 40px;
              color: #444;
            }
            .filtrate_search {
              position: relative;
              border: 1px solid #ccc;
              .el-input {
                .el-input__inner {
                  height: 24px;
                  border: none;
                  border-radius: 0;
                  border-bottom: 1px solid #ccc;
                  line-height: 24px;
                }
              }
              .el-icon-search {
                display: block;
                width: 24px;
                height: 24px;
                line-height: 24px;
                text-align: center;
                font-size: 14px;
                cursor: pointer;

                position: absolute;
                right: 0px;
                top: 0;
              }
              ul {
                width: 100%;
                height: 140px;
                overflow-y: scroll;
                li {
                  cursor: pointer;
                  line-height: 24px;
                  padding-left: 10px;
                  &:hover {
                    background-color: #409eff;
                    color: #fff;
                  }
                }
              }
            }
          }
        }
        // 筛选tag
        .checked {
          line-height: 35px;
          margin-top: 10px;
          padding-left: 20px;
          display: flex;
          & > span {
            font-size: 14px;
            font-weight: bold;
            display: inline-block;
            width: 80px;
          }
          .tagbox {
            flex: 1;
            .tag {
              margin: 0 2px;
              padding: 0 10px;
              word-wrap: break-word;
              word-break: break-all;
              overflow: hidden;
            }
          }
        }
        // 筛选按钮模块
        .screen-out {
          text-align: right;
          padding: 5px 50px;
          .result {
            margin-left: 20px;
            i {
              color: #ff7800;
              font-size: 14px;
              font-weight: bold;
            }
          }
        }
      }

      // 商品状态模块
      .commodity-state {
        margin-top: 10px;
        table {
          tr {
            th {
              border: 1px solid #ccc;
              padding: 10px 20px;
              font-size: 14px;
              cursor: pointer;
              &:hover {
                color: #fff;
                background-color: #409eff;
              }
              &.pages {
                width: 442px;
                text-align: right;
                padding-right: 10px;
                cursor: default;
                &:hover {
                  color: #000;
                  background-color: #fff;
                }
                .current-page {
                  color: #409eff;
                  font-weight: bold;
                }
              }
              i {
                font-size: 20px;
              }
            }
          }
        }
        .price-screening {
          margin: 10px 0;
          display: flex;
          align-items: center;
          .keyword {
            width: 180px;
          }

          .time {
            width: 80px;
          }

          .search-btn {
            padding: 4px 12px;
            margin: 0 30px 0 10px;
            background-color: #b7b7b7;
            color: #fff;
            &:hover {
              background-color: #0093e6;
            }
          }
          .cash-commodity {
            padding-right: 60px;
            .el-checkbox__inner {
              border: 1px solid #ccc;
            }
          }
          .conform {
            text-align: right;
            color: #f00;
            .num {
              font-weight: bold;
              font-size: 14px;
            }
          }
        }
      }

      // 商品信息列表
      .commodity-message {
        .message-list {
          & > li {
            padding: 10px;
            border-top: 1px solid #ccc;
            border-bottom: 1px solid #ccc;
            &:hover {
              background-color: #ebebeb;
            }
            // 商品图片
            .img-box {
              width: 100px;
              height: 200px;
              text-align: center;
              margin-top: 10px;
              position: relative;
              .img_small_box {
                .operation {
                  font-size: 8px;
                  display: inline-block;
                  padding: 1px 8px;
                  border: 1px solid #ccc;
                  margin: 10px 2px 0;

                  &:hover {
                    color: #ee7600;
                    border: 1px solid #ee7600;
                  }
                }
                img {
                  display: block;
                  width: 100%;
                  height: 100px;
                  border: 1px solid #eee;
                }
              }

              .big-img {
                z-index: 999;
                width: 340px;
                border: 1px solid #ccc;
                box-sizing: border-box;
                box-shadow: 4px 4px 4px #ccc;
                padding: 10px;
                background-color: #fff;
                position: absolute;
                top: -120px;
                left: 110px;
                a {
                  display: block;
                  width: 300px;
                  height: 300px;
                  margin: auto;
                  img {
                    width: 100%;
                    height: 100%;
                  }
                }

                .img-list {
                  margin-top: 10px;
                  img {
                    display: inline-block;
                    width: 50px;
                    height: 50px;
                    margin: 0 4px;
                    border: 1px solid #bdbdbd;
                    text-align: center;
                    vertical-align: middle;
                  }
                  .el-carousel__container {
                    height: 60px !important;
                    line-height: 60px;
                  }
                }
                p {
                  color: #9a9a9a;
                }
                .triangle {
                  display: block;
                  width: 0;
                  height: 0;
                  border-width: 8px 8px 8px 0;
                  border-style: solid;
                  border-color: transparent #e8e8e8 transparent transparent; /*透明 黄 透明 透明 */
                  position: absolute;
                  top: 160px;
                  left: -8px;
                }
              }
            }
          }
          // 商品信息
          .introduce {
            a {
              color: #409eff;
              font-size: 14px;
              line-height: 34px;
              display: block;
              width: 350px;
              overflow: hidden; /*超出部分隐藏*/
              text-overflow: ellipsis; /* 超出部分显示省略号 */
              white-space: nowrap; /*规定段落中的文本不进行换行 */
            }
            ul {
              li {
                color: #9a9a9a;
                display: inline-block;
                margin-right: 50px;
                line-height: 26px;
                i {
                  color: #444;
                }
              }
            }
            .handbook {
              font-size: 12px;
              line-height: 34px;
              color: #409eff;
              .el-icon-folder-opened {
                font-size: 16px;
              }
            }
            .famous {
              // display: block;
              padding: 0 5px;
              line-height: 34px;
              color: #409eff;
              border: 1px solid #409eff;
            }
          }
          // 增值税
          .tax {
            margin-top: 40px;
            ul {
              li {
                height: 26px;
                color: #444;
                p {
                  float: left;
                  width: 60%;
                  text-align: right;
                }
                span {
                  float: left;
                  color: #ff7900;
                }
              }
            }
          }
          .buy {
            margin-top: 35px;
            li {
              color: #444;
              text-align: right;
              line-height: 30px;
              i {
                color: #ff7900;
              }
            }
          }
        }
      }
    }

    //分页
    .pages {
      text-align: right;
      margin-top: 20px;
    }

    .agent-shopping {
      margin: 30px 0;
      text-align: center;
    }
  }

  // 弹窗的样式
  .el-dialog__header {
    background: #f2f2f2;
    border-bottom: 1px solid #dedede;
    .el-dialog__title {
      font-size: 14px;
      font-weight: bold;
    }
  }

  @keyframes bounceIn {
    0% {
      transform: scale(0);
      opacity: 0.4;
    }
    0% {
      transform: scale(0.7);
      opacity: 0.8;
    }
    90% {
      transform: scale(1);
      left: 630px;
      top: -480px;
    }
    100% {
      transform: scale(0);
    }
  }

  .content {
    display: flex;
    justify-content: space-around;
    font-size: 12px;
    .left {
      ul {
        li {
          line-height: 40px;
          span {
            color: #9a9a9a;
          }
        }
      }
    }
    .right {
      ul {
        li {
          line-height: 30px;
          &:nth-of-type(1) {
            text-align: center;
          }
          span {
            display: inline-block;
            width: 100px;
            text-align: right;
          }
          i {
            color: #ff7b00;
            margin-left: 5px;
          }
        }
      }
    }
  }
  .dialog-footer {
    display: flex;
    div {
      width: 300px;
    }
    ul {
      flex: 1;
      li {
        text-align: left;
        line-height: 30px;
        &:nth-of-type(1) {
          input {
            color: #ee7600;
          }

          i {
            color: #ee7600;
            &:nth-last-of-type(1) {
              font-size: 14px;
              font-weight: bold;
            }
          }
        }
        input {
          width: 60px;

          padding-left: 4px;
        }
        &:nth-last-of-type(1) {
          margin: 5px 0 20px;
          .el-button {
            padding: 10px;
          }
        }
      }
    }
  }
}
